import { useNavigate } from "react-router-dom";
import { toggleBgMode } from "./actions";

import { useDispatch, useSelector } from "react-redux";

import * as React from "react";

import { DarkModeSwitch } from "react-toggle-dark-mode";
import Switch from "./switch";

export const Routers = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const bg = useSelector((state) => state.bg);

  const handleRoute = (e) => {
    if (e.target.textContent === "Home") {
      navigate("/");
    } else {
      navigate(e.target.textContent.toLowerCase());
    }
  };

  const defaultProperties = {
    dark: {
      circle: {
        r: 1,
      },
      mask: {
        cx: "30%",
        cy: "23%",
      },
      svg: {
        transform: "rotate(40deg)",
      },
      lines: {
        opacity: 0,
      },
    },
    light: {
      circle: {
        r: 2,
      },
      mask: {
        cx: "60%",
        cy: "0%",
      },
      svg: {
        transform: "rotate(90deg)",
      },
      lines: {
        opacity: 1,
      },
    },
    springConfig: { mass: 4, tension: 250, friction: 35 },
  };

  return (
    <div className={`nav-bar ${bg ? "nav-dark" : "nav-light"}`}>
      <h3> neog health </h3>

      <div className="navigation">
        <p className="nav-link" onClick={handleRoute}>
          Home
        </p>
        <p className="nav-link" onClick={handleRoute}>
          Exercises
        </p>

        <p className="nav-link" onClick={handleRoute}>
          Foods
        </p>

        <p className="nav-link " onClick={handleRoute}>
          Goals
        </p>

        <span className="p-margin">
          <DarkModeSwitch
            style={defaultProperties}
            checked={bg}
            onChange={() => dispatch(toggleBgMode())}
            size={0}
          />
          <Switch />
        </span>
      </div>
    </div>
  );
};
